#version 100

uniform 	vec4 hlslcc_mtx4x4unity_ObjectToWorld[4];
uniform 	vec4 hlslcc_mtx4x4unity_MatrixVP[4];
uniform 	float _Outline_Thickness;
attribute highp vec3 in_POSITION0;
attribute highp vec3 in_NORMAL0;
attribute highp vec4 in_TEXCOORD0;
attribute highp vec4 in_COLOR0;
varying highp vec3 vs_INTERP0;
varying highp vec4 vs_INTERP1;
varying highp vec4 vs_INTERP2;
vec4 u_xlat0;
vec4 u_xlat1;
void main()
{
    u_xlat0.x = dot(in_NORMAL0.xyz, in_NORMAL0.xyz);
    u_xlat0.x = inversesqrt(u_xlat0.x);
    u_xlat0.xyz = u_xlat0.xxx * in_NORMAL0.xyz;
    u_xlat0.xyz = u_xlat0.xyz * vec3(vec3(_Outline_Thickness, _Outline_Thickness, _Outline_Thickness)) + in_POSITION0.xyz;
    u_xlat1.xyz = u_xlat0.yyy * hlslcc_mtx4x4unity_ObjectToWorld[1].xyz;
    u_xlat0.xyw = hlslcc_mtx4x4unity_ObjectToWorld[0].xyz * u_xlat0.xxx + u_xlat1.xyz;
    u_xlat0.xyz = hlslcc_mtx4x4unity_ObjectToWorld[2].xyz * u_xlat0.zzz + u_xlat0.xyw;
    u_xlat0.xyz = u_xlat0.xyz + hlslcc_mtx4x4unity_ObjectToWorld[3].xyz;
    u_xlat1 = u_xlat0.yyyy * hlslcc_mtx4x4unity_MatrixVP[1];
    u_xlat1 = hlslcc_mtx4x4unity_MatrixVP[0] * u_xlat0.xxxx + u_xlat1;
    u_xlat1 = hlslcc_mtx4x4unity_MatrixVP[2] * u_xlat0.zzzz + u_xlat1;
    vs_INTERP0.xyz = u_xlat0.xyz;
    gl_Position = u_xlat1 + hlslcc_mtx4x4unity_MatrixVP[3];
    vs_INTERP1 = in_TEXCOORD0;
    vs_INTERP2 = in_COLOR0;
    return;
}

#version 100

#ifdef GL_FRAGMENT_PRECISION_HIGH
    precision highp float;
#else
    precision mediump float;
#endif
precision highp int;
uniform 	vec4 _Outline_Color;
varying highp vec4 vs_INTERP2;
#define SV_TARGET0 gl_FragData[0]
vec4 u_xlat0;
void main()
{
    u_xlat0.x = ((gl_FrontFacing ? 1 : 0) != 0) ? 0.0 : 1.0;
    u_xlat0.w = u_xlat0.x * vs_INTERP2.w;
    u_xlat0.xyz = vs_INTERP2.xyz * _Outline_Color.xyz;
    SV_TARGET0 = u_xlat0;
    return;
}
